import { Component , OnInit } from '@angular/core';
import { FormBuilder,FormGroup, Validator, Validators } from '@angular/forms';
@Component({
  selector: 'app-reservation-form',
  templateUrl: './reservation-form.component.html',
  styleUrls: ['./reservation-form.component.css']
})
export class ReservationFormComponent implements OnInit{



  reservationForm: FormGroup= new FormGroup({});

  constructor(private formBuilder:FormBuilder){

  }
  ngOnInit(): void {
    this.reservationForm = this.formBuilder.group({
      checkInDate: [ '',Validators.required],
      checkOutDate: [ '',Validators.required],
      guestName: ['' ,Validators.required],
      guestEmail: ['' ,[Validators.required,Validators.email]],
      roomNumber: [ '',Validators.required],
    })
  }

  onSubmit(){
    if(this.reservationForm.valid){
      console.log("valid");
    }
  }
}
<form [formGroup]="reservationForm" (ngSubmit)="onSubmit()">


    <div>

        <label for="">Check In Date: </label>
        <input type="date" formControlName="checkInDate">
        <div *ngIf="reservationForm.get('checkInDate')?.invalid">
            Check In Date is required       
        </div>
    </div>
    <div>

        <label for="">Check Out Date: </label>
        <input type="date" formControlName="checkOutDate">
    </div>
    <div>

        <label for="">Guest Name: </label>
        <input type="text" formControlName="guestName">
    </div>
    <div>

        <label for="">Guest Email: </label>
        <input type="text" formControlName="guestEmail">
    </div>
    <div>

        <label for="">Room number: </label>
        <input type="number" formControlName="roomNumber">
    </div>

<button type="submit"  [disabled]="reservationForm.invalid">Submit</button>
</form>
